INSERT INTO recetas (rec_estado,rec_categoria,rec_imagen,rec_nombre,rec_preparacion) values(1,"Carne","/images/personas1.jpg","Bistec encebollado","*Cortar cebollas en juliana. - *Calentar aceite de oliva en sartén y freír la cebolla. - *Luego de la cebolla, cocinar el bistec y sasonar a gusto.")
INSERT INTO recetas (rec_estado,rec_categoria,rec_imagen,rec_nombre,rec_preparacion) values(1,"Carne","/images/personas2.jpg","Ternera con verduras","*Cortar ternera, zanahoria y cebolla en tiras largas. - *Preparar un wok con una pizca de aceite a fuego lento. - *Cocinar hasta que la cebolla adquiera una tonalidad translúcida.")
INSERT INTO recetas (rec_estado,rec_categoria,rec_imagen,rec_nombre,rec_preparacion) values(1,"Ensalada","/images/personas3.jpeg","Ensalada mixta con arroz","*Cortar tomate y zanahoria en cubos, agregar unas hojas de lechuga. - *Hervir una taza de arroz. - *En un plato, agregar una capa de arroz y otra de verduras. - *Agregar mix de semillas.")
INSERT INTO recetas (rec_estado,rec_categoria,rec_imagen,rec_nombre,rec_preparacion) values(1,"Ensalada","/images/personas1.jpg","Salteado de morrón y cebolla con fideos","*Cortar cebolla y morrón en juliana y freír juntos. - *Hervir una taza de fideos integrales. - *En un plato, agregar una capa de fideos y otra del salteado.")
INSERT INTO recetas (rec_estado,rec_categoria,rec_imagen,rec_nombre,rec_preparacion) values(1,"Pan","/images/personas2.jpg","Pan casero de avena","*Preparar una mezcla de harina integral, 1 huevo, polvo para hornear, 2 cucharadas de avena y agua. - *Realizar una mecla uniforme y vertir sobre un molde. - *Añadir mix de semillas y hornear por 1 hora. - *Dejar reposar.")
INSERT INTO recetas (rec_estado,rec_categoria,rec_imagen,rec_nombre,rec_preparacion) values(1,"Pescado","/images/personas3.jpeg","Atún con arroz","*Hervir una taza de arroz. - *Incorporar 1/4 de lata de atún al natural. - *Añadir 2 tomates cortados en rodajas.")
INSERT INTO recetas (rec_estado,rec_categoria,rec_imagen,rec_nombre,rec_preparacion) values(1,"Pescado","/images/personas1.jpg","Corvina con verduras","*Precalentar el horno a 180 grados y preparar una bandeja o fuente grande con una porción de papel de aluminio. - *Pelar ligeramente las zanahorias y cortar en bastones. - *Añadir la zanahoria, limón y aceite. - *Hornear por 5-8 minutos.")
INSERT INTO recetas (rec_estado,rec_categoria,rec_imagen,rec_nombre,rec_preparacion) values(1,"Pollo","/images/personas2.jpg","Tortilla de pollo","*Cortar la pechuga en trocitos, la cebolla y el morrón también. - *En una sartén pochar la cebolla, añadir el morrón y a continuación, cuando esté blanco, el pollo. - *Batir los huevos con sal y añadir a la sartén con el sofrito. Cuajar la tortilla.")
